import{U as u}from"./UmlScript.44c1627f.js";import{f as C,o as B,c as D,d as A,u as e,e as E}from"./app.cc1a521e.js";import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";const t=E('<p><em>\uBCF8\uBB38\uC740 2019\uB144\uB3C4 \uD558\uBC18\uAE30 \uC544\uC8FC\uB300\uD559\uAD50\uC758 \uCEF4\uD4E8\uD130\uACF5\uD559\uACFC \uAD50\uC218\uB85C \uC7AC\uC9C1\uD558\uC168\uB358 \uAE40\uC0C1\uD6C8 \uAD50\uC218\uB2D8\uC758 \uC6B4\uC601\uCCB4\uC81C \uACFC\uBAA9 \uAC15\uC758 \uC790\uB8CC\uB97C \uAE30\uBC18\uC73C\uB85C \uC791\uC131\uB418\uC5C8\uC74C\uC744 \uC54C\uB9BD\uB2C8\uB2E4.</em></p><h2 id="_1-\u1112\u1167\u11AB\u1103\u1162-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u1103\u1169\u11BC\u110C\u1161\u11A8-\u1109\u116E\u11AB\u1109\u1165" tabindex="-1"><a class="header-anchor" href="#_1-\u1112\u1167\u11AB\u1103\u1162-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u1103\u1169\u11BC\u110C\u1161\u11A8-\u1109\u116E\u11AB\u1109\u1165" aria-hidden="true">#</a> 1. \uD604\uB300 \uC6B4\uC601\uCCB4\uC81C\uC758 \uB3D9\uC791 \uC21C\uC11C</h2><ol><li>PDU(Power Distribution Unit, \uC804\uC6D0\uBD84\uBC30\uC7A5\uCE58)\uAC00 \uC2DC\uC2A4\uD15C\uC758 CPU\uC640 \uC7A5\uCE58\uB4E4\uC5D0\uAC8C \uC804\uC6D0\uC744 \uACF5\uAE09\uD55C\uB2E4</li><li>\uBD80\uD2B8\uC2A4\uD2B8\uB7A9 \uD504\uB85C\uADF8\uB7A8(e.g., BIOS) \uC790\uB3D9 \uB85C\uB4DC</li><li>Boot loader(e.g., grub)\uAC00 OS \uCEE4\uB110 \uB85C\uB4DC \uBC0F \uC2E4\uD589</li><li>BIOS \u2192 Boot loader \u2192 kernel phase 1 \u2192 kernel phase 2</li></ol><p>\uCEF4\uD4E8\uD130\uAC00 \uCF1C\uC9C8 \uB54C \uCC98\uC74C\uBD80\uD130 \uC6B4\uC601\uCCB4\uC81C\uC758 \uCF54\uB4DC\uB97C \uC77D\uC744 \uC218 \uC5C6\uB2E4. \uAE30\uBCF8\uC801\uC73C\uB85C \uC6B4\uC601\uCCB4\uC81C\uB294 File system\uC774\uB77C\uB294 \uD615\uD0DC\uC778\uB370, \uC774\uB294 \uBAB9\uC2DC \uBCF5\uC7A1\uD558\uB2E4. \uB530\uB77C\uC11C \uC6B4\uC601\uCCB4\uC81C\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C \uADF8 File system \uC55E\uBD80\uBD84\uC5D0 Bootloader \uB77C\uB294, \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC758 \uC77C\uBD80 \uB0B4\uC6A9\uC744 \uAC04\uB2E8\uD788 \uD3EC\uD568\uD558\uACE0 \uC788\uB294 \uD504\uB85C\uADF8\uB7A8\uC744 \uB2F4\uACE0 \uC788\uACE0, \uCEF4\uD4E8\uD130\uAC00 \uC2E4\uD589\uB420 \uB54C Boot loader\uB97C \uC2E4\uD589\uC2DC\uD0B4\uC73C\uB85C\uC368 bootstrap\uC744 \uD589\uD55C\uB2E4(\uC6B4\uC601\uCCB4\uC81C \uC2E4\uD589).</p><h2 id="_2-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166-\u1100\u116E\u110C\u1169-\u110C\u1169\u11BC\u1105\u1172" tabindex="-1"><a class="header-anchor" href="#_2-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166-\u1100\u116E\u110C\u1169-\u110C\u1169\u11BC\u1105\u1172" aria-hidden="true">#</a> 2. \uC6B4\uC601\uCCB4\uC81C \uAD6C\uC870 \uC885\uB958</h2><ul><li>Simple - MS-DOS</li><li>Monolithic - UNIX</li><li>Layered(Abstraction-based)</li><li>Microkernel - Mach</li><li>Hybrid - Modern OSes</li></ul><h2 id="_2-1-simple" tabindex="-1"><a class="header-anchor" href="#_2-1-simple" aria-hidden="true">#</a> 2.1. Simple</h2><p>Simple structure\uB97C \uB530\uB974\uB294 \uB300\uD45C\uC801\uC778 OS\uB294 MS-DOS\uAC00 \uC788\uB2E4. Simple structure\uB294 \uCD5C\uC18C\uD55C\uC758 \uC800\uC7A5 \uACF5\uAC04\uC5D0 \uBAA8\uB4E0 \uAE30\uB2A5\uC744 \uB2F4\uAE30 \uC704\uD55C \uBAA9\uC801\uC73C\uB85C \uB9CC\uB4E4\uC5B4\uC84C\uB2E4. \uC644\uBCBD\uD788 \uBAA8\uB4C8\uD654\uB418\uC5B4 \uC124\uACC4\uB418\uC9C0 \uC54A\uC558\uACE0, \uC778\uD130\uD398\uC774\uC2A4\uC640 \uAE30\uB2A5 \uACC4\uCE35\uC774 \uBD84\uB9AC\uB418\uC5B4 \uC788\uC9C0 \uC54A\uB2E4.</p><p>\uC5B4\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uD558\uB4DC\uC6E8\uC5B4\uC5D0 \uC9C1\uC811 \uC811\uADFC \uAC00\uB2A5\uD588\uB2E4(user mode, kernel mode \uC874\uC7ACx).</p><div class="mxgraph" style="max-width:100%;margin:auto;" data-mxgraph="{&quot;highlight&quot;:&quot;#0000ff&quot;,&quot;lightbox&quot;:false,&quot;nav&quot;:true,&quot;edit&quot;:&quot;_blank&quot;,&quot;xml&quot;:&quot;&lt;mxfile host=\\&quot;app.diagrams.net\\&quot; modified=\\&quot;2022-07-15T05:20:51.689Z\\&quot; agent=\\&quot;5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/103.0.0.0 Safari/537.36\\&quot; etag=\\&quot;XSRn7jj-2kybDV_DlfuQ\\&quot; version=\\&quot;20.1.1\\&quot; type=\\&quot;google\\&quot;&gt;&lt;diagram id=\\&quot;8GutSCba3COvzZyJwegq\\&quot; name=\\&quot;\uADF8\uB9BC 1. Simple Structure - MS-DOS\\&quot;&gt;&lt;/diagram&gt;&lt;/mxfile&gt;&quot;}"></div><div class="align-center">&lt;\uADF8\uB9BC 1. Simple Structure - MS-DOS&gt;</div><h2 id="_2-2-monolithic" tabindex="-1"><a class="header-anchor" href="#_2-2-monolithic" aria-hidden="true">#</a> 2.2. Monolithic</h2><p>Monolithic\uC758 \uC0AC\uC804\uC801 \uC758\uBBF8\uB294 <em>The Big Mess</em>\uB85C \uD558\uB098\uC758 \uD070 SW \uC2DC\uC2A4\uD15C\uC744 \uC758\uBBF8\uD55C\uB2E4. Monolithic structure\uB294 Simple structure\uC640 \uAD6C\uC870\uC801 \uCC28\uC774\uB294 \uC5C6\uC73C\uB098 \uADF8 \uD06C\uAE30\uC640 \uADDC\uBAA8\uAC00 \uBC29\uB300\uD574\uC9C4 \uAC83\uC744 \uB73B\uD55C\uB2E4(\uD544\uC790\uC758 \uCD94\uCE21\uC785\uB2C8\uB2E4). Kernel structure\uB77C\uACE0\uB3C4 \uD55C\uB2E4. Monolihic structure\uB97C \uB530\uB974\uB294 \uB300\uD45C\uC801\uC778 OS\uB294 UNIX\uAC00 \uC788\uB2E4. \uC778\uD130\uD398\uC774\uC2A4\uC640 \uAE30\uB2A5 \uACC4\uCE35\uC774 \uBD84\uB9AC\uB418\uC5B4 \uC788\uC9C0 \uC54A\uACE0, \uC2DC\uC2A4\uD15C \uCF5C \uC778\uD130\uD398\uC774\uC2A4\uC640 \uBB3C\uB9AC\uC801\uC778 \uD558\uB4DC\uC6E8\uC5B4 \uC0AC\uC774\uC758 \uBAA8\uB4E0 \uAE30\uB2A5(\uD30C\uC77C \uC2DC\uC2A4\uD15C, CPU \uC2A4\uCF00\uC974\uB9C1, \uBA54\uBAA8\uB9AC \uAD00\uB9AC, \uC785\uCD9C\uB825 \uB4F1)\uC774 \uB2E8\uC77C \uACC4\uCE35\uC5D0 \uAD6C\uD604\uB418\uC5B4 \uC788\uB2E4.</p><p>Monolithic\uC740 \uCC98\uB9AC \uC18D\uB3C4\uC640 \uC131\uB2A5\uC774 \uC88B\uB2E4\uB294 \uC7A5\uC810\uC774 \uC788\uC73C\uB098, \uC720\uC9C0\uBCF4\uC218 \uBC0F \uC5C5\uB370\uC774\uD2B8\uAC00 \uD798\uB4E4\uACE0 \uBCF4\uC548\uACFC \uC548\uC815\uC131, \uC2E0\uB8B0\uC131, \uD655\uC7A5\uC131\uC744 \uAC16\uAE30 \uC5B4\uB835\uB2E4.</p><ul><li>\uBAA8\uB4E0 OS \uCF54\uB4DC\uAC00 \uB2E8\uC77C \uBA54\uBAA8\uB9AC \uACF5\uAC04\uC5D0\uC11C \uB3D9\uC791, OS\uB97C \uAD6C\uC131\uD558\uB294 \uC5B4\uB5A4 \uAE30\uB2A5\uC774\uB77C\uB3C4 \uC2DC\uC2A4\uD15C\uC5D0\uC11C \uB3D9\uC791 \uB4F1\uC758 \uBB38\uC81C\uAC00 \uC788\uC74C</li></ul><h2 id="_2-3-layered" tabindex="-1"><a class="header-anchor" href="#_2-3-layered" aria-hidden="true">#</a> 2.3. Layered</h2><p>\uC6B4\uC601\uCCB4\uC81C \uB0B4\uBD80\uC801\uC73C\uB85C \uC5EC\uB7EC \uAC1C\uC758 \uACC4\uCE35\uC73C\uB85C \uB098\uB258\uC5B4 \uC0C1\uC704 \uACC4\uCE35\uC740 \uD558\uC704 \uACC4\uCE35\uC758 \uC778\uD130\uD398\uC774\uC2A4\uB9CC\uC744 \uC774\uC6A9\uD558\uC5EC \uB3D9\uC791\uD558\uB3C4\uB85D \uC124\uACC4\uD55C Layered \uC544\uD0A4\uD14D\uCC98\uB97C \uC0AC\uC6A9\uD55C\uB2E4. Layered \uC544\uD0A4\uD14D\uCC98\uC758 \uD2B9\uC9D5\uC740 \uC0C1\uC704 \uACC4\uCE35\uC774 \uD558\uC704 \uACC4\uCE35\uC744 \uC774\uC6A9\uD560 \uB54C \uCD94\uC0C1\uD654 \uAE30\uB2A5\uC744 \uC774\uC6A9\uD55C \uC778\uD130\uD398\uC774\uC2A4\uB97C \uD1B5\uD574\uC11C\uB9CC \uC218\uD589\uD55C\uB2E4\uB294 \uB9D0\uC774\uBA70, \uC774\uB7F0 \uAC1C\uB150\uC774 \uD6C4\uC220\uD560 Microkernel \uC544\uD0A4\uD14D\uCC98\uC758 OS\uB97C \uAC1C\uBC1C\uD558\uB294\uB370 \uC0AC\uC6A9\uB410\uB2E4.</p><h2 id="_2-4-microkernel" tabindex="-1"><a class="header-anchor" href="#_2-4-microkernel" aria-hidden="true">#</a> 2.4. Microkernel</h2><p>\uAE30\uB2A5\uC774\uB098 \uBAA8\uB4C8 \uB2E8\uC704\uB85C \uAD6C\uC131 \uC694\uC18C\uB4E4\uC744 \uBD84\uB9AC\uD558\uACE0 \uBA54\uC138\uC9C0 \uD050\uB97C \uD65C\uC6A9\uD558\uC5EC \uAD6C\uC131 \uC694\uC18C\uB4E4\uC774 \uAC01\uAC01 \uC644\uC804\uD788 \uB3C5\uB9BD\uC801\uC778 \uAE30\uB2A5\uC744 \uC218\uD589\uD558\uB3C4\uB85D \uC124\uACC4\uD55C \uAD6C\uC870\uB97C Microkernel \uC544\uD0A4\uD14D\uCC98\uB77C \uD55C\uB2E4. Mode\uC758 \uAC1C\uB150\uC774 \uC788\uC5B4 user mode, kernel mode \uB4F1\uC744 \uAC00\uC9C4\uB2E4.</p><p>Microkernel \uC544\uD0A4\uD14D\uCC98\uB294 Monolithic\uC758 \uC7A5\uB2E8\uC810\uC744 \uBC18\uB300\uB85C \uAC00\uC9C0\uACE0 \uC788\uB2E4. \uC548\uC815\uC131, \uC2E0\uB8B0\uC131, \uD655\uC7A5\uC131\uC744 \uAC00\uC9C0\uBA70 \uC720\uC9C0\uBCF4\uC218\uAC00 \uC26C\uC6B4 \uBC18\uBA74 \uBA54\uC138\uC9C0 \uD050\uB97C \uC774\uC6A9\uD55C \uAE30\uB2A5 \uAC04 \uD1B5\uC2E0\uC774\uB098 \uBAA8\uB4DC \uAC04 \uC804\uD658 \uB4F1\uC5D0 \uB300\uD55C \uC624\uBC84\uD5E4\uB4DC\uAC00 \uC874\uC7AC\uD558\uC5EC \uC131\uB2A5\uC774 \uBE44\uAD50\uC801 \uB5A8\uC5B4\uC9C4\uB2E4.</p><p>\uCC98\uC74C\uC73C\uB85C Microkernel \uC544\uD0A4\uD14D\uCC98\uC758 OS\uB85C \uC18C\uAC1C\uB410\uB358 Mach\uB294 \uC2A4\uB808\uB4DC \uC2A4\uCF00\uC974\uB9C1, \uBA54\uC2DC\uC9C0 \uD328\uC2F1(\uD1B5\uC2E0), \uAC00\uC0C1 \uBA54\uBAA8\uB9AC, \uC7A5\uCE58 \uB4DC\uB77C\uC774\uBC84 \uAE30\uB2A5\uB9CC\uC744 \uD3EC\uD568\uD588\uB2E4. IBM OS/2, Windows NT/XP, MacOS X \uB4F1\uC774 Mach\uC758 \uC77C\uBD80 \uAD6C\uC870\uB97C \uAE30\uBC18\uC73C\uB85C \uAC1C\uBC1C\uB410\uB2E4.</p><h2 id="_2-5-hybrid" tabindex="-1"><a class="header-anchor" href="#_2-5-hybrid" aria-hidden="true">#</a> 2.5. Hybrid</h2><p>\uD604\uB300\uC758 \uC6B4\uC601\uCCB4\uC81C\uB4E4\uC740 \uC704\uC5D0\uC11C \uC18C\uAC1C\uD55C \uD2B9\uC815 \uBAA8\uB378\uC774\uB098 \uC544\uD0A4\uD14D\uCC98\uAC00 \uC544\uB2C8\uB77C \uC5EC\uB7EC \uAC00\uC9C0 \uBAA8\uB378\uC744 \uC774\uC6A9\uD558\uC5EC \uC124\uACC4\uB41C\uB2E4. \uC131\uB2A5\uACFC \uBCF4\uC548, \uC0AC\uC6A9\uC131 \uB4F1\uC5D0\uC11C \uD544\uC694 \uC2DC \uC801\uC808\uD55C \uBAA8\uB378\uC744 \uC2DC\uC2A4\uD15C \uB0B4\uBD80\uC5D0 \uC801\uC7AC\uC801\uC18C\uC5D0 \uC801\uC6A9\uD558\uC5EC \uC124\uACC4\uB41C\uB2E4.</p><p>\uC608\uB97C \uB4E4\uC5B4 Linux\uC640 Solaris\uB294 \uC804\uCCB4\uC801\uC73C\uB85C Layered \uC544\uD0A4\uD14D\uCC98\uB97C \uB530\uB77C \uC124\uACC4\uB410\uC9C0\uB9CC address performance\uAC00 \uD544\uC694\uD55C OS kernel \uC601\uC5ED\uC740 \uB2E8\uC77C kernel address space\uB97C \uC0AC\uC6A9\uD558\uB294 Monolithic \uC124\uACC4\uB97C \uB530\uB978\uB2E4. \uC708\uB3C4\uC6B0\uB294 \uC804\uCCB4\uC801\uC73C\uB85C Monolithic \uC124\uACC4\uB97C \uB530\uB974\uC9C0\uB9CC \uBD80\uBD84\uC801\uC73C\uB85C \uD558\uC704 \uC2DC\uC2A4\uD15C\uB4E4\uC740 Microkernel\uB85C \uC124\uACC4\uB410\uB2E4. Android, MacOS\uB3C4 Hybrid \uC544\uD0A4\uD14D\uCC98\uB85C \uC124\uACC4\uB410\uB2E4.</p>',24),i=C({__name:"03-os-structure.html",setup(o){return(a,l)=>(B(),D("div",null,[t,A(e(u))]))}}),h=r(i,[["__file","03-os-structure.html.vue"]]);export{h as default};
