import{U as u}from"./UmlScript.44c1627f.js";import{f as C,o as B,c as D,d as A,u as t,e}from"./app.94e44163.js";import{_ as i}from"./_plugin-vue_export-helper.cdc0426e.js";const l=e('<p><em>\uBCF8\uBB38\uC740 2019\uB144\uB3C4 \uD558\uBC18\uAE30 \uC544\uC8FC\uB300\uD559\uAD50\uC758 \uCEF4\uD4E8\uD130\uACF5\uD559\uACFC \uAD50\uC218\uB85C \uC7AC\uC9C1\uD558\uC168\uB358 \uAE40\uC0C1\uD6C8 \uAD50\uC218\uB2D8\uC758 \uC6B4\uC601\uCCB4\uC81C \uACFC\uBAA9 \uAC15\uC758 \uC790\uB8CC\uB97C \uAE30\uBC18\uC73C\uB85C \uC791\uC131\uB418\uC5C8\uC74C\uC744 \uC54C\uB9BD\uB2C8\uB2E4.</em></p><h2 id="_1-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1175\u11AF\u1112\u1162\u11BC-\u1110\u1173\u11A8\u110C\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1175\u11AF\u1112\u1162\u11BC-\u1110\u1173\u11A8\u110C\u1175\u11BC" aria-hidden="true">#</a> 1. \uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uD2B9\uC9D5</h2><p>\uD504\uB85C\uC138\uC2A4\uB294 I/O \uC791\uC5C5 \uBE48\uB3C4\uC640 \uC2E4\uD589 \uC2DC\uAC04\uC5D0 \uB530\uB77C \uC790\uC6D0 \uC0AC\uC6A9\uB7C9(CPU Burst \uB4F1)\uC774 \uBCC0\uD569\uB2C8\uB2E4.</p><ul><li>I/O \uC791\uC5C5 \uBE48\uB3C4\uAC00 \uB192\uC744 \uC218\uB85D CPU\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB294 Idle \uC0C1\uD0DC\uAC00 \uB9CE\uC74C</li><li>\uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uC9C1\uD6C4 \uCCAB 5ms \uC815\uB3C4\uAE4C\uC9C0 CPU \uC0AC\uC6A9 \uCD5C\uB300\uD654</li></ul><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1p22CCZUru6xkqepN3WsTHQL9iM-B4Ieb" alt="" loading="lazy"> &lt;\uADF8\uB9BC 1. \uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uD2B9\uC9D5 - CPU-bound vs IO-bound&gt;</p><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1h219IifMzdR4hVc-mFfbeuuIBpASXimD" alt="" loading="lazy"> &lt;\uADF8\uB9BC 2. \uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uD2B9\uC9D5 - CPU frequency by burst duration&gt;</p><h2 id="_2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC\u110B\u1175\u1105\u1161\u11AB" tabindex="-1"><a class="header-anchor" href="#_2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC\u110B\u1175\u1105\u1161\u11AB" aria-hidden="true">#</a> 2. \uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1\uC774\uB780?</h2><p>\uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1(Process Scheduling)\uC774\uB780 \uC5B8\uC81C, \uC5B4\uB5A4 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC790\uC6D0\uC744 \uD560\uB2F9\uD560\uC9C0 \uACB0\uC815\uD558\uB294 \uAC83\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1\uC758 \uBAA9\uC801\uC740 <strong>CPU\uC640 \uAC19\uC740 \uC790\uC6D0\uC758 \uC0AC\uC6A9\uB960\uC744 \uCD5C\uB300\uD654\uD558\uB294 \uAC83</strong>\uC785\uB2C8\uB2E4. \uD504\uB85C\uC138\uC2A4\uB97C \uC2E4\uD589\uD558\uB294 \uC790\uC6D0\uC774 \uBCF4\uD1B5 CPU\uC774\uAE30 \uB54C\uBB38\uC5D0 CPU \uC2A4\uCF00\uC904\uB9C1\uC774\uB77C\uACE0\uB3C4 \uD569\uB2C8\uB2E4.</p><p>\uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1\uC740 \uB2E4\uC74C\uACFC \uAC19\uC740 \uAE30\uB2A5\uC744 \uC218\uD589\uD569\uB2C8\uB2E4.</p><ul><li>\uC2A4\uCF00\uC904\uB9C1\uC758 \uB300\uC0C1\uC774 \uB420 \uAC00\uC6A9 \uD504\uB85C\uC138\uC2A4 \uAD00\uB9AC</li><li>\uB2E4\uC74C\uC73C\uB85C \uC2E4\uD589\uD560 \uD504\uB85C\uC138\uC2A4 \uACB0\uC815</li><li>\uD504\uB85C\uC138\uC2A4 \uAC04 \uC804\uD658(\uBB38\uB9E5 \uAD50\uD658)</li></ul><h2 id="_2-\u1106\u116E\u11AB\u1106\u1162\u11A8-\u1100\u116D\u1112\u116A\u11AB-context-switch" tabindex="-1"><a class="header-anchor" href="#_2-\u1106\u116E\u11AB\u1106\u1162\u11A8-\u1100\u116D\u1112\u116A\u11AB-context-switch" aria-hidden="true">#</a> 2. \uBB38\uB9E5 \uAD50\uD658(Context-switch)</h2><p>\uD504\uB85C\uC138\uC2A4 \uAC04 \uC804\uD658 \uC2DC \uC804 \uD504\uB85C\uC138\uC2A4\uB098 \uC55E\uC73C\uB85C \uC2E4\uD589\uD560 \uD504\uB85C\uC138\uC2A4\uC758 \uC0C1\uD0DC\uB97C \uC800\uC7A5\uD574\uB193\uC740 \uB370\uC774\uD130\uB97C \uBB38\uB9E5(Context)\uB77C \uD569\uB2C8\uB2E4. \uC774 \uB370\uC774\uD130\uB294 \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D(PCB, Process Control Block)\uC5D0 \uC800\uC7A5\uB418\uC5B4 \uC788\uC2B5\uB2C8\uB2E4. \uD504\uB85C\uC138\uC2A4 \uC804\uD658 \uC2DC \uC774 Context\uB97C \uAD50\uD658\uD558\uC5EC \uC2E4\uD589\uC911\uC774\uB358 \uD504\uB85C\uC138\uC2A4\uC758 \uB370\uC774\uD130\uC640 \uC0C1\uD0DC\uB97C \uC800\uC7A5\uD558\uACE0, \uB2E4\uC74C\uC73C\uB85C \uC2E4\uD589\uD560 \uD504\uB85C\uC138\uC2A4\uC758 \uC9C0\uB09C \uB370\uC774\uD130\uC640 \uC0C1\uD0DC\uB97C \uBD88\uB7EC\uC640 \uC791\uC5C5\uC744 \uC774\uC5B4 \uC9C4\uD589\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4.</p><p>\uADF8\uB7F0\uB370 \uC774 \uBB38\uB9E5 \uAD50\uD658\uC744 \uD558\uB294 \uC911\uC5D0\uB294 CPU\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB294 idle \uC0C1\uD0DC\uAC00 \uB429\uB2C8\uB2E4. \uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1\uC758 \uBAA9\uC801\uC774 CPU\uC758 \uC0AC\uC6A9\uB960\uC744 \uCD5C\uB300\uD654\uD558\uB294 \uAC83\uC774\uBBC0\uB85C \uC801\uC808\uD55C \uBE48\uB3C4\uB85C \uBB38\uB9E5 \uAD50\uD658\uC744 \uD558\uC5EC idle \uC0C1\uD0DC\uC758 \uC2DC\uAC04\uC744 \uCD5C\uC18C\uD654\uD558\uB294 \uAC83\uC774 \uC88B\uC2B5\uB2C8\uB2E4.</p><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1VBRL0FPgR3VRtsmgmDO6Zk03aS7XCBm2" alt="" loading="lazy"> &lt;\uADF8\uB9BC 3. \uBB38\uB9E5 \uAD50\uD658(Context-switch)&gt;</p><h2 id="_3-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1175\u110C\u1165\u11B7\u1100\u116A-\u1100\u1162\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#_3-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1175\u110C\u1165\u11B7\u1100\u116A-\u1100\u1162\u110B\u116D" aria-hidden="true">#</a> 3. \uC2A4\uCF00\uC904\uB9C1 \uC2DC\uC810\uACFC \uAC1C\uC694</h2><p>\uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1 \uC2DC\uC810\uC740 \uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC \uB2E4\uC774\uC5B4\uADF8\uB7A8\uC5D0\uC11C \uC0C1\uD0DC \uC804\uC774\uC5D0 \uD574\uB2F9\uD558\uB294 \uBAA8\uB4E0 \uAD6C\uAC04\uC5D0 \uD574\uB2F9\uD569\uB2C8\uB2E4.</p><div class="mxgraph" style="max-width:100%;margin:auto;" data-mxgraph="{&quot;highlight&quot;:&quot;#0000ff&quot;,&quot;lightbox&quot;:false,&quot;nav&quot;:true,&quot;edit&quot;:&quot;_blank&quot;,&quot;xml&quot;:&quot;&lt;mxfile host=\\&quot;app.diagrams.net\\&quot; modified=\\&quot;2022-08-02T12:25:17.916Z\\&quot; agent=\\&quot;5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/103.0.0.0 Safari/537.36\\&quot; etag=\\&quot;inJpRCsXFW5pH1QNhoLQ\\&quot; version=\\&quot;20.2.2\\&quot; type=\\&quot;google\\&quot;&gt;&lt;diagram id=\\&quot;bzSGDfPCPLQ6wK0j2PPz\\&quot; name=\\&quot;Page-1\\&quot;&gt;&lt;/diagram&gt;&lt;/mxfile&gt;&quot;}"></div><div class="align-center">&lt;\uADF8\uB9BC 4. Scheduling points&gt;</div><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1GM6EDcNwkkzy3DAO-FPyLcSnum8EF2F-" alt="" loading="lazy"> &lt;\uADF8\uB9BC 5. \uC2A4\uCF00\uC904\uB9C1 \uAC1C\uC694&gt;</p><p>\uC2DC\uC2A4\uD15C\uC740 CPU\uC640 \uAC19\uC740 \uD504\uB85C\uC138\uC11C\uB97C \uD1B5\uD574 \uD504\uB85C\uC138\uC2A4\uB97C \uC2E4\uD589\uD558\uB2E4\uAC00 I/O \uC694\uCCAD\uC774\uB098 \uC2DC\uBD84\uD560 \uC54C\uACE0\uB9AC\uC998, \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131(fork child process), \uC778\uD130\uB7FD\uD2B8 \uB4F1\uC758 \uC774\uBCA4\uD2B8\uC5D0 \uC758\uD574 \uD504\uB85C\uC138\uC2A4\uC758 \uC0C1\uD0DC \uC804\uC774\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uC2A4\uCF00\uC904\uB7EC\uB294 \uB2E4\uC74C\uC73C\uB85C \uC2E4\uD589\uD560 \uD504\uB85C\uC138\uC2A4\uB97C \uC804\uB2EC(dispatch)\uD558\uC5EC \uBB38\uB9E5 \uAD50\uD658\uC774 \uBC1C\uC0DD\uD569\uB2C8\uB2E4.</p><h2 id="_4-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u110C\u1175\u1111\u116D-\u1100\u1175\u110C\u116E\u11AB-criteria" tabindex="-1"><a class="header-anchor" href="#_4-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u110C\u1175\u1111\u116D-\u1100\u1175\u110C\u116E\u11AB-criteria" aria-hidden="true">#</a> 4. \uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uC9C0\uD45C(\uAE30\uC900, Criteria)</h2><p>\uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998\uC758 \uC131\uB2A5\uC744 \uD3C9\uAC00\uD558\uB294 \uC694\uC18C\uB85C \uD06C\uAC8C 5\uAC00\uC9C0\uAC00 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li>CPU \uC0AC\uC6A9\uB960(CPU utilization) <ul><li>\uC804\uCCB4 \uC2DC\uC2A4\uD15C \uC2DC\uAC04 \uC911 CPU\uAC00 \uC791\uC5C5\uC744 \uCC98\uB9AC\uD558\uB294 \uC2DC\uAC04\uC758 \uBE44\uC728</li></ul></li><li>\uCC98\uB9AC\uC728(Throughput) <ul><li>\uB2E8\uC704 \uC2DC\uAC04(Time unit) \uB2F9 \uC2E4\uD589 \uC644\uB8CC\uD558\uB294 \uD504\uB85C\uC138\uC2A4 \uC218</li></ul></li><li>\uC18C\uC694 \uC2DC\uAC04(Turnaround time) <ul><li>\uD504\uB85C\uC138\uC2A4 \uCC98\uB9AC \uC2DC\uC791\uBD80\uD130 \uC644\uB8CC\uAE4C\uC9C0 \uC18C\uC694\uB41C \uC2DC\uAC04</li></ul></li><li>\uB300\uAE30 \uC2DC\uAC04(Waiting time) <ul><li>\uD504\uB85C\uC138\uC2A4\uAC00 \uB300\uAE30 \uD050(Ready queue)\uC5D0 \uC785\uB825\uB41C \uC9C1\uD6C4 \uC2E4\uD589(\uC790\uC6D0 \uD560\uB2F9 \uBC1B\uAE30)\uAE4C\uC9C0 \uB300\uAE30\uD558\uB294 \uC2DC\uAC04</li></ul></li><li>\uC751\uB2F5 \uC2DC\uAC04(Response time) <ul><li>\uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uC694\uCCAD \uC9C1\uD6C4\uBD80\uD130 \uACB0\uACFC \uBC18\uD658\uAE4C\uC9C0 \uC18C\uC694\uB41C \uC2DC\uAC04</li></ul></li></ul><h2 id="_5-\u1100\u1175\u110B\u1161\u110B\u116A-\u1109\u1165\u11AB\u110C\u1165\u11B7\u1112\u1167\u11BC-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_5-\u1100\u1175\u110B\u1161\u110B\u116A-\u1109\u1165\u11AB\u110C\u1165\u11B7\u1112\u1167\u11BC-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" aria-hidden="true">#</a> 5. \uAE30\uC544\uC640 \uC120\uC810\uD615 \uC2A4\uCF00\uC904\uB9C1</h2><p>\uAE30\uC544(Starvation)\uB780 \uD2B9\uC815 \uD504\uB85C\uC138\uC2A4\uAC00 \uC2E4\uD589(\uC9C4\uD589, Progress)\uC5D0 \uD544\uC694\uD55C \uC790\uC6D0\uC744 \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uAC00 \uC810\uC720\uD558\uACE0 \uC788\uC5B4 \uAE34 \uC2DC\uAC04 \uB3D9\uC548 \uC2E4\uD589\uB418\uC9C0 \uC54A\uB294 \uD604\uC0C1\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uC88B\uC9C0 \uC54A\uC740 \uC2A4\uCF00\uC904\uB9C1 \uC815\uCC45\uC740 \uAE30\uC544 \uD604\uC0C1\uC744 \uBC1C\uC0DD\uC2DC\uD0B5\uB2C8\uB2E4.</p><p>\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uAC00 \uC810\uC720\uD55C \uC790\uC6D0(ex. CPU)\uC744 \uD2B9\uC815 \uD504\uB85C\uC138\uC2A4\uAC00 \uAC00\uB85C\uCC4C \uC218 \uC5C6\uB294 \uD615\uD0DC\uC758 \uC2A4\uCF00\uC904\uB9C1 \uC815\uCC45\uC744 \uBE44\uC120\uC810\uD615(Non-preemptive) \uC2A4\uCF00\uC904\uB9C1\uC774\uB77C \uD558\uACE0, \uBC18\uB300\uB85C \uAC00\uB85C\uCC4C \uC218 \uC788\uB294 \uC2A4\uCF00\uC904\uB9C1 \uC815\uCC45\uC744 \uC120\uC810\uD615(Preemptive) \uC2A4\uCF00\uC904\uB9C1\uC774\uB77C \uD569\uB2C8\uB2E4. \uB458\uC758 \uCC28\uC774\uB97C \uBE44\uAD50\uD558\uBA74 \uB2E4\uC74C\uACFC \uAC19\uC2B5\uB2C8\uB2E4.</p><div class="align-center"><table><thead><tr><th style="text-align:center;">\uAD00\uC810</th><th style="text-align:center;">\uBE44\uC120\uC810\uD615 \uC2A4\uCF00\uC904\uB9C1</th><th style="text-align:center;">\uC120\uC810\uD615 \uC2A4\uCF00\uC904\uB9C1</th></tr></thead><tbody><tr><td style="text-align:center;">\uAC1C\uB150</td><td style="text-align:center;">\uD55C \uBC88 \uD504\uB85C\uC138\uC2A4\uC5D0\uAC8C \uC790\uC6D0\uC774 \uD560\uB2F9\uB418\uBA74 \uD504\uB85C\uC138\uC2A4\uAC00 \uC644\uB8CC\uB418\uAC70\uB098 \uC790\uBC1C\uC801\uC73C\uB85C Waiting \uC0C1\uD0DC\uB85C \uC804\uC774\uD558\uAE30 \uC804\uAE4C\uC9C0 \uC790\uC6D0\uC744 \uC810\uC720\uD558\uACE0 \uC788\uC74C</td><td style="text-align:center;">\uC2A4\uCF00\uC904\uB7EC\uAC00 \uAE30\uC900\uC5D0 \uB530\uB77C \uD504\uB85C\uC138\uC2A4\uB97C \uC911\uB2E8\uD558\uACE0 \uBB38\uB9E5 \uAD50\uD658\uC744 \uAC15\uC81C\uD560 \uC218 \uC788\uC74C</td></tr><tr><td style="text-align:center;">\uAE30\uC544</td><td style="text-align:center;">\uC2E4\uD589 \uC2DC\uAC04\uC774 \uAE34 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC758\uD574 \uBC1C\uC0DD</td><td style="text-align:center;">\uB300\uAE30 \uD050(Ready Queue)\uC5D0 \uC6B0\uC120\uC21C\uC704\uAC00 \uB192\uC740 \uD504\uB85C\uC138\uC2A4\uAC00 \uC790\uC8FC \uC785\uB825\uB418\uBA74 \uC6B0\uC120\uC21C\uC704\uAC00 \uB0AE\uC740 \uD504\uB85C\uC138\uC2A4\uAC00 \uAE30\uC544\uB97C \uACAA\uC744 \uC218 \uC788\uC74C</td></tr><tr><td style="text-align:center;">\uC120\uC810\uC73C\uB85C \uC778\uD55C \uC624\uBC84\uD5E4\uB4DC</td><td style="text-align:center;">\uC874\uC7AC\uD558\uC9C0 \uC54A\uC74C</td><td style="text-align:center;">\uC874\uC7AC</td></tr><tr><td style="text-align:center;">\uB300\uAE30 \uC2DC\uAC04(Waiting Time)</td><td style="text-align:center;">(\uBE44\uAD50\uC801)\uB0AE\uC74C</td><td style="text-align:center;">\uB192\uC74C</td></tr><tr><td style="text-align:center;">\uC751\uB2F5 \uC2DC\uAC04(Response Time)</td><td style="text-align:center;">\uBE60\uB984</td><td style="text-align:center;">\uB290\uB9BC</td></tr><tr><td style="text-align:center;">CPU \uC0AC\uC6A9\uB960</td><td style="text-align:center;">\uB0AE\uC74C</td><td style="text-align:center;">\uB192\uC74C</td></tr></tbody></table><div class="align-center">&lt;\uD45C 1. Non-preemptive scheduling vs Preemptive scheduling&gt;</div></div><p>\uC120\uC810\uD615 \uC2A4\uCF00\uC904\uB9C1 \uC815\uCC45\uC744 \uC0AC\uC6A9\uD560 \uC2DC \uB2E4\uC74C\uACFC \uAC19\uC740 \uAC83\uB4E4\uC744 \uACE0\uB824\uD574\uC57C \uD569\uB2C8\uB2E4.</p><ul><li>\uACF5\uC720 \uB370\uC774\uD130\uC5D0 \uB300\uD574 \uC5C5\uB370\uC774\uD2B8 \uC911\uC778 \uD504\uB85C\uC138\uC2A4\uAC00 \uC911\uAC04\uC5D0 \uC120\uC810\uB418\uBA74 \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD574\uC57C \uD558\uB098</li><li>System call\uC744 \uC2E4\uD589\uC911\uC778 \uD504\uB85C\uC138\uC2A4\uAC00 \uC911\uAC04\uC5D0 \uC120\uC810\uB41C \uACBD\uC6B0 \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD574\uC57C \uD558\uB098</li></ul><h2 id="_6-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7" tabindex="-1"><a class="header-anchor" href="#_6-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7" aria-hidden="true">#</a> 6. \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</h2><p>\uC2A4\uCF00\uC904\uB9C1 \uC815\uCC45\uC744 \uAD6C\uD604\uD558\uB294 \uC54C\uACE0\uB9AC\uC998\uC740 \uB2E4\uC591\uD558\uBA70 \uC2DC\uC2A4\uD15C\uC774 \uB3D9\uC791\uD558\uB294 \uB3C4\uBA54\uC778\uC774\uB098 \uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uB530\uB77C \uB2E4\uB978 \uBAA9\uC801\uC73C\uB85C \uC124\uACC4\uB418\uB294\uB370 \uAC04\uB2E8\uD788 \uBD84\uB958\uD558\uC5EC \uC0B4\uD3B4\uBCF4\uBA74 \uB2E4\uC74C\uACFC \uAC19\uC2B5\uB2C8\uB2E4.</p><ul><li>\uACF5\uD1B5(Commonly) <ul><li>No starvation</li><li>Fairness - \uACF5\uD3C9\uD55C \uC218\uC900\uC758 \uC790\uC6D0 \uACF5\uC720</li><li>Balance - \uC2DC\uC2A4\uD15C\uC758 \uBAA8\uB4E0 \uC694\uC18C\uAC00 busy\uD558\uAC8C \uB3D9\uC791\uD560 \uAC83</li></ul></li><li>\uBC30\uCE58 \uC2DC\uC2A4\uD15C(Batch systems) <ul><li>CPU \uC0AC\uC6A9\uB7C9(CPU utilization) \uCD5C\uB300\uD654</li></ul></li><li>\uBC18\uC751\uD615 \uC2DC\uC2A4\uD15C(Interactive systems) <ul><li>\uC751\uB2F5 \uC2DC\uAC04(Response time) \uCD5C\uC18C\uD654</li></ul></li></ul><p>\uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998\uC740 FCFS, SJF, SRTF, Priority, RR, Multilevel Queue \uB4F1\uC774 \uC788\uB294\uB370 \uBCF8\uBB38\uC5D0\uC11C \uCC28\uB840\uB85C \uC124\uBA85\uD560 \uC608\uC815\uC785\uB2C8\uB2E4.</p><h3 id="_6-1-fcfs" tabindex="-1"><a class="header-anchor" href="#_6-1-fcfs" aria-hidden="true">#</a> 6.1. FCFS</h3><p>FCFS\uB294 First-Come, First-Served\uC758 \uC57D\uC790\uB85C \uADF8 \uB73B\uB300\uB85C \uBA3C\uC800 \uB300\uAE30 \uD050\uC5D0 \uC785\uB825\uB41C \uD504\uB85C\uC138\uC2A4 \uC21C\uC73C\uB85C \uCC98\uB9AC\uD55C\uB2E4\uB294 \uC54C\uACE0\uB9AC\uC998\uC785\uB2C8\uB2E4.</p><p>FCFS \uC2A4\uCF00\uC904\uB9C1 \uBC29\uC2DD\uC740 <strong>Convoy effect</strong> \uBB38\uC81C\uB97C \uAC16\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">Info</p><p>Convoy effect\uB780 \uCC98\uB9AC \uC2DC\uAC04\uC774 \uAE34 \uD504\uB85C\uC138\uC2A4\uAC00 \uB300\uAE30 \uD050\uC5D0 \uBA3C\uC800 \uC785\uB825\uB418\uACE0 \uADF8 \uB4A4\uC5D0 \uCC98\uB9AC \uC2DC\uAC04\uC774 \uC9E7\uC740 \uD504\uB85C\uC138\uC2A4\uAC00 \uC785\uB825\uB410\uC744 \uB54C \uC55E\uC120 \uD504\uB85C\uC138\uC2A4\uC758 \uAE34 \uCC98\uB9AC \uC2DC\uAC04 \uB54C\uBB38\uC5D0 \uD504\uB85C\uC138\uC2A4 \uC804\uCCB4 \uD3C9\uADE0 \uC18C\uC694 \uC2DC\uAC04(Average turnaround time)\uC774 \uC99D\uAC00\uD558\uB294 \uBB38\uC81C\uB97C \uB9D0\uD569\uB2C8\uB2E4.</p></div><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1mA0SmVzRrTVKRYK1YH1xNg5rCIAnMW-a" alt="" loading="lazy"> &lt;\uADF8\uB9BC 6. FCFS \uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uACC4\uC0B0&gt;</p><h3 id="_6-2-sjf" tabindex="-1"><a class="header-anchor" href="#_6-2-sjf" aria-hidden="true">#</a> 6.2. SJF</h3><p>SJF\uB294 Shortest Job First\uC758 \uC57D\uC790\uB85C, \uADF8 \uB73B\uB300\uB85C \uC608\uC0C1 CPU burst(\uD504\uB85C\uC138\uC2A4\uAC00 CPU\uB97C \uC0AC\uC6A9\uD558\uB294 \uC2DC\uAC04, \uC989 \uCC98\uB9AC \uC2DC\uAC04)\uAC00 \uAC00\uC7A5 \uC791\uC740 Job\uC744 \uBA3C\uC800 \uC2E4\uD589\uD558\uB294 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998\uC785\uB2C8\uB2E4. \uBA3C\uC800 \uC2E4\uD589\uC911\uC778 \uD504\uB85C\uC138\uC2A4\uAC00 \uCC98\uB9AC \uC644\uB8CC\uB410\uC744 \uB54C \uB300\uAE30 \uD050\uC5D0 \uC874\uC7AC\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uB4E4 \uC911 CPU burst\uAC00 \uAC00\uC7A5 \uC791\uC740 \uD504\uB85C\uC138\uC2A4\uB97C \uC2A4\uCF00\uC904\uB9C1\uD558\uB294 \uBC29\uC2DD\uC73C\uB85C \uBE44\uC120\uC810\uD615\uC785\uB2C8\uB2E4.</p><ul><li>\uD2B9\uC9D5 <ul><li>\uCD5C\uC18C \uD3C9\uADE0 \uB300\uAE30 \uC2DC\uAC04(Miminum average waiting time)\uC744 \uBCF4\uC7A5(Guarantee)\uD558\uB294 \uC54C\uACE0\uB9AC\uC998\uC784</li><li>\uBE44\uC120\uC810\uD615</li><li>\uC6B0\uC120\uC21C\uC704(Priority)\uB97C \uACE0\uB824\uD55C \uC2A4\uCF00\uC904\uB9C1</li></ul></li><li>\uBB38\uC81C <ul><li>\uAE30\uC544 \uAC00\uB2A5\uC131 \uC788\uC74C <ul><li><strong>Aging</strong>, <strong>Priority boosting</strong> \uAE30\uBC95\uC744 \uC774\uC6A9\uD574 \uD574\uACB0 \uAC00\uB2A5</li></ul></li><li>\uD504\uB85C\uC138\uC2A4\uC758 \uC608\uC0C1 \uCC98\uB9AC \uC2DC\uAC04, CPU burst\uB97C \uCE21\uC815\uD558\uB294 \uAC83\uC740 \uBD88\uAC00\uB2A5 <ul><li>\uBBF8\uB798\uC758 CPU burst\uB97C \uD569\uB9AC\uC801\uC73C\uB85C \uCD94\uCE21\uD560 \uC218 \uC788\uB294 \uBC29\uBC95 \uD544\uC694</li></ul></li></ul></li></ul><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1Mnv7DmPkm8WU8J9KfdtEgbznI7DHNS-C" alt="" loading="lazy"> &lt;\uADF8\uB9BC 7. SJF \uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uACC4\uC0B0&gt;</p><h3 id="_6-3-srtf" tabindex="-1"><a class="header-anchor" href="#_6-3-srtf" aria-hidden="true">#</a> 6.3. SRTF</h3><p>SRTF\uB294 Shortest Remaining Time First\uC758 \uC57D\uC790\uB85C, \uADF8 \uB73B\uB300\uB85C \uB0A8\uC740 \uCC98\uB9AC \uC2DC\uAC04\uC774 \uC9E7\uC740 \uD504\uB85C\uC138\uC2A4 \uC21C\uC73C\uB85C \uCC98\uB9AC\uD558\uB294 \uC54C\uACE0\uB9AC\uC998\uC785\uB2C8\uB2E4. \uC55E\uC11C \uC124\uBA85\uD55C SJF \uC2A4\uCF00\uC904\uB9C1\uC744 \uC120\uC810\uD615 \uBC29\uC2DD\uC73C\uB85C \uC218\uD589\uD558\uB294 \uAE30\uBC95\uC785\uB2C8\uB2E4.</p><p>\uB300\uAE30 \uD050\uC5D0 \uC0C8 \uD504\uB85C\uC138\uC2A4\uAC00 \uC785\uB825\uB420 \uB54C\uB9C8\uB2E4 \uB300\uAE30 \uD050\uC5D0 \uC874\uC7AC\uD558\uB294 \uBAA8\uB4E0 \uD504\uB85C\uC138\uC2A4\uC640 \uC2E4\uD589\uC911\uC778 \uD504\uB85C\uC138\uC2A4 \uC911 \uB0A8\uC740 \uCC98\uB9AC \uC2DC\uAC04\uC774 \uAC00\uC7A5 \uC9E7\uC740 \uD504\uB85C\uC138\uC2A4\uB97C \uC120\uD0DD\uD558\uC5EC \uC2E4\uD589\uD569\uB2C8\uB2E4.</p><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1ZNE4MmUTntmi_7gN4JNgd9s-qajQaygQ" alt="" loading="lazy"> &lt;\uADF8\uB9BC 8. SRTF \uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uACC4\uC0B0&gt;</p><h3 id="rr" tabindex="-1"><a class="header-anchor" href="#rr" aria-hidden="true">#</a> RR</h3><p>\uC2E4\uD589 \uD050(Run Queue)\uAC00 \uC21C\uD658\uD615(Circular) FIFO(First-In, First-Out) \uD050\uB85C \uC124\uACC4\uB418\uC5B4 \uAC01 Job\uB9C8\uB2E4 Time slice(\uB610\uB294 Scheduling quantum\uC774\uB77C \uD568)\uB97C \uD560\uB2F9\uBC1B\uACE0 \uADF8 \uC2DC\uAC04 \uB3D9\uC548 \uC2E4\uD589\uB41C \uB4A4 \uB2E4\uC74C \uD504\uB85C\uC138\uC2A4\uB85C \uC804\uD658\uB418\uB294 \uC54C\uACE0\uB9AC\uC998\uC785\uB2C8\uB2E4.</p><ul><li>\uC5EC\uB7EC \uAC1C\uC758 Timer-interrupt \uB610\uB294 Timer tick\uC744 \uC774\uC6A9\uD574 Time slice \uACB0\uC815</li><li>Time slice\uC758 \uD06C\uAE30\uAC00 \uCEE4\uC9C0\uBA74 \uC2DC\uC2A4\uD15C \uBC18\uC751\uC131 \uC800\uD558(Less responsive)</li><li>Time slice\uC758 \uD06C\uAE30\uAC00 \uC791\uC544\uC9C0\uBA74 \uBB38\uB9E5 \uAD50\uD658 \uC624\uBC84\uD5E4\uB4DC \uC99D\uAC00(Higher context-switch overhead)</li><li>Time slice\uC758 \uD06C\uAE30\uB294 \uBCF4\uD1B5 10~100ms\uB85C \uC815\uD574\uC9D0 <ul><li>\uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uD2B9\uC9D5 &lt;\uADF8\uB9BC 1&gt;, &lt;\uADF8\uB9BC 2&gt; \uADFC\uAC70</li></ul></li></ul><p class="align-center"><img src="https://drive.google.com/uc?export=view&amp;id=1-Chjm3R4M8SwpTHQXYWU5Sr5Rig7fOmF" alt="" loading="lazy"> &lt;\uADF8\uB9BC 9. Round Robin \uC2A4\uCF00\uC904\uB9C1&gt;</p><ul><li>\uD2B9\uC9D5 <ul><li>\uC120\uC810\uD615</li><li>\uAE30\uC544 X</li><li>\uC2DC\uBD84\uD560(Time-sharing)\uC744 \uD1B5\uD55C \uC751\uB2F5 \uC2DC\uAC04 \uD5A5\uC0C1(Improve reponse time)</li><li>\uB300\uAE30 \uC2DC\uAC04(Waiting time)\uC740 \uC99D\uAC00\uD568</li></ul></li></ul><h3 id="\u1103\u1161\u1103\u1161\u11AB\u1100\u1168-\u110F\u1172" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u1103\u1161\u11AB\u1100\u1168-\u110F\u1172" aria-hidden="true">#</a> \uB2E4\uB2E8\uACC4 \uD050</h3><p>(Multilevel Queue) \uC791\uC131 \uC608\uC815</p><h3 id="\u1103\u1161\u1103\u1161\u11AB\u1100\u1168-\u1111\u1175\u1103\u1173\u1107\u1162\u11A8-\u110F\u1172" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u1103\u1161\u11AB\u1100\u1168-\u1111\u1175\u1103\u1173\u1107\u1162\u11A8-\u110F\u1172" aria-hidden="true">#</a> \uB2E4\uB2E8\uACC4 \uD53C\uB4DC\uBC31 \uD050</h3><p>(Multilevel Feedback Queue) \uC791\uC131 \uC608\uC815</p>',55),a=C({__name:"07-process-scheduling.html",setup(r){return(E,n)=>(B(),D("div",null,[l,A(t(u))]))}}),c=i(a,[["__file","07-process-scheduling.html.vue"]]);export{c as default};
